INSERT INTO public.zgony (id, imie_nazwisko, wiek, miejsce_urodzenia, data_zgonu, przyczyna_zgonu, inne_wazne_informacje, source_file, image_url, parafia) VALUES (31, 'Franciszka Stasiak', '37', 'Łódź', '20. dzień bieżącego roku (1940), godzina 12:00', 'brak informacji', 'Akt zgonu sporządzono 2 stycznia 1940 roku o godzinie 12:00 w kancelarii parafii Matki Boskiej Zwycięskiej w Łodzi. Franciszka Stasiak była córką Jana i Agnieszki z Rządków Stanków (nazwisko ''Stanków'' trudne do odczytania). Była żoną Kazimierza Stasiaka. Zmarła zamieszkiwała w Łodzi. Świadkami byli Kazimierz Stasiak (wdowiec), zamieszkały w Łodzi, oraz Anastazja Dolsa (robotnica), zamieszkała w Łodzi. Akt został przeczytany, przyjęty i podpisany. Urzędnik Stanu Cywilnego, Ksiądz: Władysław Gurowski. Świadkowie (podpisy): K. Stasiak, A. Dolsa. Należy zauważyć, że data zgonu w akcie (''20. dnia bieżącego roku'') jest chronologicznie po dacie sporządzenia aktu (2 stycznia 1980), co sugeruje możliwy błąd pisarski w dokumencie (np. zamiast ''ubiegłego roku'' lub brakuje miesiąca).', 'wynik.json', '\static\parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi\1940 rok\2\22.jpg', 'Parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi');
INSERT INTO public.zgony (id, imie_nazwisko, wiek, miejsce_urodzenia, data_zgonu, przyczyna_zgonu, inne_wazne_informacje, source_file, image_url, parafia) VALUES (34, 'Pelagia Wilczyńska', '62 lata', 'Siemianowo', '2 stycznia 1940', 'brak informacji', 'Akt nr 11. Data sporządzenia aktu: 5 stycznia 1940, godz. 10:00, w kancelarii parafii Matki Boskiej Zwycięskiej w Łodzi. Zmarła w Łodzi. Rodzice: Jakub i Józefa z Przyborowskich, ostatnio z domu Rogowskich. Wdowa po Edmundzie Milewskim. Zamieszkała w Łodzi. Świadkowie: Edmund Milewski (rymarz), Stanisław Piotrowski (szkolny), obaj zamieszkali w Łodzi. Urzędnik Stanu Cywilnego, Ksiądz: Władysław Gurorski.', 'wynik.json', '\static\parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi\1940 rok\4\41.jpg', 'Parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi');
INSERT INTO public.zgony (id, imie_nazwisko, wiek, miejsce_urodzenia, data_zgonu, przyczyna_zgonu, inne_wazne_informacje, source_file, image_url, parafia) VALUES (35, 'Józef Limański', '30 lat', 'wieś Drewno, powiat Koło', '20 grudnia 1939', 'brak informacji', 'Akt nr 13. Data sporządzenia aktu: 5 stycznia 1940, godz. 11:00, w kancelarii parafii Matki Boskiej Zwycięskiej w Łodzi. Zmarł w Łodzi. Rodzice: Seweryn i Stanisława z Limańskich. Żonaty z Zofią z Paprockich. Zamieszkały w Łodzi, przynależny do gminy Koło. Świadkowie: Józef Michalski (robotnik), Franciszek Limański (robotnik), obaj zamieszkali w Łodzi. Urzędnik Stanu Cywilnego, Ksiądz: Władysław Gurorski.', 'wynik.json', '\static\parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi\1940 rok\4\41.jpg', 'Parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi');
