INSERT INTO public.zgony (id, imie_nazwisko, wiek, miejsce_urodzenia, data_zgonu, przyczyna_zgonu, inne_wazne_informacje, source_file, image_url, parafia) VALUES (40, 'Wilhelm Grajlich', '60', 'Łódź', '18 stycznia 1940', 'brak informacji', 'Zgłoszenie zgonu nastąpiło 19 stycznia 1940 roku o godzinie 15:00 w kancelarii parafii Matki Boskiej Imiejnskiej w Łodzi. Świadkowie pełnoletni: Helena Grajlich (żona zmarłego), zamieszkała w Łodzi; Franciszek Libisz (Inżhalto), zamieszkały w Łodzi. Zmarł o godzinie 12:00 w Łodzi. Rodzice: Wilhelm i Otylia z Kubasików, małżonkowie Grajlichowie. Żona: Helena z Libiszów. Akt ten został przeczytany, przyjęty i podpisany. Urzędnik Stanu Cywilnego, Ksiądz: Władysław Gurowski. Świadek podpisu: Helena Grajlichowa Libisz.', 'wynik.json', '\static\parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi\1940 rok\5\52.jpg', 'Parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi');
INSERT INTO public.zgony (id, imie_nazwisko, wiek, miejsce_urodzenia, data_zgonu, przyczyna_zgonu, inne_wazne_informacje, source_file, image_url, parafia) VALUES (41, 'Kazimierz Domalążek', '18', 'Łódź', '3 stycznia 1940', 'brak informacji', 'Zgłoszenie zgonu nastąpiło 8 stycznia 1940 roku o godzinie 12:00 w kancelarii parafii Matki Boskiej Imiejnskiej w Łodzi. Świadkowie pełnoletni: Stanisława Domalążek, zamieszkała w Łodzi; Józef Kasak (dworna), zamieszkały w Łodzi. Zmarł o godzinie niewiadomej w Łodzi. Rodzice: Franciszek i Stanisława z Kubasików, małżonkowie Domalążek. Akt ten został przeczytany, przyjęty i podpisany przez Nas i drugiego świadka, pierwszy niepiśmienny. Urzędnik Stanu Cywilnego, Ksiądz: Władysław Gurowski. Świadek podpisu: J. Słowirow.', 'wynik.json', '\static\parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi\1940 rok\5\52.jpg', 'Parafia rzymskokatolickiej Matki Boskiej Zwycięskiej w Łodzi');
